import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import cookie from 'react-cookies';
import {Redirect} from 'react-router';
import {Navbar} from './Navbar';
import {Link} from 'react-router-dom';
import { FaUser } from 'react-icons/fa';
import './index.css';
import {Sidenavbar} from './Sidenavbar';
import axios from 'axios';
import { FaPlusCircle } from 'react-icons/fa';

export class Grades extends Component{

    constructor(){
        super();
        this.state = {  
            Assignments : [],
            authFlag:false
        }
        
    }
    



    //get the books data from backend  
    componentDidMount(){
        const {course_id} = this.props.match.params
        let id= {course_id};
        axios.get(`http://localhost:3001/grades/${id.course_id}`)
                .then((response) => {
                //update the state with the response data
                this.setState({
                    Assignments : this.state.Assignments.concat(response.data) 
                });
            });
    }
    render(){


               //iterate over courses to create a table row
               let details = this.state.Assignments.map(item => {
                   if(item.graded=="no")
                   {var button_type=<button class="btn btn-success" disabled>not graded</button>;

                   }else{
                    var button_type=<button class="btn btn-success">{item.graded}</button>;
                   }

                return(
                    <tr>
                        <td>{item.course_id}</td>
                        <td>{item.assignment_name}</td>
                        <td>{item.assignment_duedate}</td>
                        <td>{item.assignment_points}</td>
                        <td>{button_type}</td>
                    
                       
                         </tr>
                )
            })
           //redirect based on successful login
           let redirectVar = null;
           let no_access = null;
           if(!cookie.load('username')){
               redirectVar = <Redirect to= "/login"/>
           }
           if(cookie.load('user_type')=="faculty"){
            no_access=<Redirect to= "/dashboard"/>
        }
        return(
            

            <div>
            {redirectVar}
            {no_access}
            <Navbar/>

        <div class="side_navigation">

            <div class="row">
                <div class="col-md-2 col-sm-2">
                   
                <Sidenavbar/>
                </div>
                <div class="col-md-10 col-sm-10">
                <br/>
            
                <br/>
                <br/>
                <h3>Assignment grades</h3>
<table class="table">
                            <thead>
                                <tr>
                                    <th>Course ID</th>
                                    <th>Assignment Name</th>
                                    <th>Due date</th>
                                    <th>Points</th>

                                
                                </tr>
                            </thead>
                            <tbody>
                                {/*Display the Tbale row based on data recieved*/}
                                {details}
                            </tbody>
                        </table>
                
                </div>
            </div>

        </div>
     </div>

        )

}
}
