import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import cookie from 'react-cookies';
import {Redirect} from 'react-router';
import {Navbar} from './Navbar';
import {Link} from 'react-router-dom';
import { FaUser } from 'react-icons/fa';
import './index.css';
import {Sidenavbar} from './Sidenavbar';
import axios from 'axios';
import Draggable, {DraggableCore} from 'react-draggable'; 

export class Courses extends Component{

    constructor(){
        super();
        this.state = {  
            courses : [],
            authFlag:false,
            deltaPosition: {
                x: 0, y: 0
              }
        }
    }
    
    componentDidUpdate() {

        if (this.state.authFlag==true) {
        axios.get('http://localhost:3001/courses')
        .then((response) => {
        //update the state with the response data
        this.setState({
            courses :response.data ,
            authFlag:false
           
        });
    });

        }

    }

    remove=(e)=>{



        const value = e.target.value;
       
const data={

    course_id:value
}

axios.post('http://localhost:3001/remove_course',data)
.then(response => {
    if(response.status === 200){
        this.setState({
            authFlag : true
        })
    }else{
        this.setState({
            authFlag : false
        })
    }
});

}


handleDrag(e, ui) {
    const {x, y} = this.state.deltaPosition;
    this.setState({
      deltaPosition: {
        x: x + ui.deltaX,
        y: y + ui.deltaY,
      }
    });
  }

  onStart() {
    this.setState({activeDrags: ++this.state.activeDrags});
  }

  onStop() {
    this.setState({activeDrags: --this.state.activeDrags});
  }

    //get the books data from backend  
    componentDidMount(){
        axios.get('http://localhost:3001/courses')
                .then((response) => {
                //update the state with the response data
                this.setState({
                    courses : this.state.courses.concat(response.data) 
                });
            });
    }
    render(){
        const dragHandlers = {onStart: this.onStart, onStop: this.onStop};
        const {deltaPosition, controlledPosition} = this.state;

               //iterate over courses to create a table row
               let details = this.state.courses.map(item => {
                return(
                    <Draggable
                    axis="x"
                   
                    defaultPosition={{x: 100, y: 100}}
                    position={null}
                    grid={[25, 25]}
                    scale={1}
                    onDrag={this.handleDrag} {...dragHandlers}
                   >
                    <div class="col-md-4">
                        <div class="course-box">
                            <h4>Course ID: {item.course_id}</h4>
                            <h5>{item.course_name}</h5>
                            <hr/>
                            <p>{item.course_description}</p>
                            <button class="btn btn-danger" onClick={this.remove} value={item.course_id}>Remove</button>
                            <div>x: {deltaPosition.x.toFixed(0)}, y: {deltaPosition.y.toFixed(0)}</div>

                        </div>
                    </div>
                    </Draggable>
     
                )
            })
           //redirect based on successful login
           let redirectVar = null;
           let no_access = null;
           if(!cookie.load('username')){
               redirectVar = <Redirect to= "/login"/>
           }
           if(cookie.load('user_type')=="faculty"){
            no_access=<Redirect to= "/dashboard"/>
        }
    
        return(
            
            <div>
            {redirectVar}
            {no_access}
            <Navbar/>

        <div class="side_navigation">

            <div class="row">
                <div class="col-md-2 col-sm-2">
                   
                <Sidenavbar/>
                </div>
                <div class="col-md-10 col-sm-10">
                <h3>Courses enrolled by you</h3>
<div class="row">
                                {details}
                                </div>
                </div>
            </div>

        </div>
     </div>

        )

}
}
