import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import cookie from 'react-cookies';
import {Redirect} from 'react-router';
import {Navbar} from './Navbar';
import {Link} from 'react-router-dom';
import { FaUser } from 'react-icons/fa';
import './index.css';
import {Sidenavbar} from './Sidenavbar';
import axios from 'axios';


export class Inbox extends Component{

    constructor(){
        super();
        this.state = {  
            new_message:"",
            messages : [],
            authFlag : false
        }
        this.sendMessage=this.sendMessage.bind(this);
    }  
    //get the books data from backend  
    componentDidMount(){

        axios.get('http://localhost:3001/inbox')
                .then((response) => {
                //update the state with the response data
                this.setState({
                    messages : this.state.messages.concat(response.data) 
                });
            });
    }


sendMessage=(e)=>{

    this.setState({
        new_message:e.target.value
    })
}


submitMessage = (e) => {

    //prevent page from refresh
    e.preventDefault();
    const data = {

        message : this.state.new_message

    }

    //set the with credentials to true
    axios.defaults.withCredentials = true;

    //make a post request with the user data
    axios.post('http://localhost:3001/send_message',data)
        .then(response => {
            console.log("Status Code : ",response.status);
            if(response.status === 200){
                this.setState({
                    authFlag : true
                })


            }else{
                this.setState({
                    authFlag : false
                })
                alert("there is some error");
            }
        });
  }


  componentDidUpdate() {

    if (this.state.authFlag==true) {
        axios.get('http://localhost:3001/inbox')
    .then((response) => {
    //update the state with the response data
    this.setState({
        messages :response.data ,
        authFlag:false

    });
});

    }

}

    render(){



                       //iterate over courses to create a table row
                       let details = this.state.messages.map(item => {
                        return(
                            <tr>
                                <td>{item.from}</td>
                                <td>{item.message}</td>
                                <td>{item.date}</td>

                            </tr>
                        )
                    })
           //redirect based on successful login
           let redirectVar = null;
           let no_access = null;
           if(!cookie.load('username')){
               redirectVar = <Redirect to= "/login"/>
           }
           if(cookie.load('user_type')=="student"){
            no_access=<Redirect to= "/dashboard"/>
        }
        return(

        <div>
                {redirectVar}
                {no_access}
                <Navbar/>

            <div class="side_navigation">

                <div class="row">
                    <div class="col-md-2 col-sm-2">

                    <Sidenavbar/>
                    </div>
                    <div class="col-md-10 col-sm-10">
                    <h3>Send Message</h3>
                    <input type="text" name="message"   className="form-control" placeholder="Enter the Message" 
                                        onChange={this.sendMessage} /><br/>
                                        <button class="btn btn-success full"onClick={this.submitMessage}>SUBMIT</button>
<br/>
<br/>
                    <h3>Your Announcements</h3>


                    <table class="table">
                            <thead>
                                <tr>
                                    <th>From</th>
                                    <th>Message</th>
                                    <th>Date</th>
                                </tr>
                            </thead>
                            <tbody>
                                {/*Display the Tbale row based on data recieved*/}
                                {details}
                            </tbody>
                        </table>




                    </div>
                </div>

            </div>
         </div>
        )

}
}
